var fs = require('fs');
var path = require('path');
var _ = require('underscore');

exports.types =  {
  "css": "text/css",
  "gif": "image/gif",
  "html": "text/html",
  "ico": "image/x-icon",
  "jpeg": "image/jpeg",
  "jpg": "image/jpeg",
  "js": "text/javascript",
  "json": "application/json",
  "pdf": "application/pdf",
  "png": "image/png",
  "svg": "image/svg+xml",
  "swf": "application/x-shockwave-flash",
  "tiff": "image/tiff",
  "txt": "text/plain",
  "wav": "audio/x-wav",
  "wma": "audio/x-ms-wma",
  "wmv": "video/x-ms-wmv",
  "xml": "text/xml"
};

var map = {
    'compressed': ['zip', 'rar', 'gz', '7z'],
    'text': ['txt', 'md', 'ini', 'json'],
    'developer': ['meta', 'dll'],
    'image': ['jpg', 'jpeg', 'png', 'gif', 'bmp', 'tga'],
    'pdf': ['pdf'],
    'css': ['css'],
    'html': ['html'],
    'word': ['doc', 'docx'],
    'powerpoint': ['ppt', 'pptx'],
    'movie': ['mkv', 'avi', 'rmvb', 'mp4'],
    'music': ['mp3', 'wav'],
};

var cached = {};

exports.stat = function (filepath) {
    var result = {
        name: path.basename(filepath),
        path: filepath,
    };

    try {
        var stat = fs.statSync(filepath);
        if (stat.isDirectory()) {
            var isDrive = process.platform == 'win32' && filepath.slice(-1) == ':';
            if (isDrive) {
                result.name = filepath;
                result.type = 'drive';
            }
            else {
                result.type = 'folder';
            }
        }
        else {
            var ext = path.extname(filepath).substr(1);
            result.type = cached[ext];
            if (!result.type) {
                for (var key in map) {
                    if (_.include(map[key], ext)) {
                        cached[ext] = result.type = key;
                        break;
                    }
                }

                if (!result.type)
                    result.type = 'blank';  // unknown type
            }
        }
    }
    catch (e) {
        //console.log(e);
        //window.alert(e);
        if (!result.type)
            result.type = 'undefined';
    }

    return result;
}
